func (c *current) onConstraint1(cons any) (any, error) {
	switch cons := cons.(type) {
	case Constraint:
		return cons, nil
	case Limit:
		return &ExactConstraint{Value: cons}, nil
	default:
		return nil, fmt.Errorf("unexpected constraint type: %T", cons)
	}
}

